			pass = pass+1;
			else
			failMap.put("Failure in UserLicense-License Issuer","Actual:"+resGetUser.'**'.'ns13:userProfileResponse'.'ns13:userProfileResList'[i].'ns8:newValue'.text()+"--Expected:"+cUL_issuer)
		}
		else if(resGetUser.'**'.'ns13:userProfileResponse'.'ns13:userProfileResList'[i].'ns8:attributeName'.text() =="Expiration Date")
		{base = base+1;
		if (flagcreateUserLicenseExp ==0)
		{

			if ((resGetUser.'**'.'ns13:userProfileResponse'.'ns13:userProfileResList'[i].'ns8:newValue'.text() == cUL_expirationDate) && (resGetUser.'**'.'ns13:userProfileResponse'.'ns13:userProfileResList'[i].'ns8:action'.text() == "Create"))
			{pass = pass+1;flagcreateUserLicenseExp=1;}
			else
			{//failMap.put(i+":"+resGetUser.'**'.'ns13:userProfileResponse'.'ns13:userProfileResList'[i].'ns8:newValue'.text(),cUL_expirationDate)
				failMap.put("Failure in UserLicense-Expiration Date","Actual:"+resGetUser.'**'.'ns13:userProfileResponse'.'ns13:userProfileResList'[i].'ns8:newValue'.text()+"--Expected:"+cUL_expirationDate)
			flagcreateUserLicenseExp=1;}

		}
		else
		{log.info "update User Lic - Expiration Check"
			if ((resGetUser.'**'.'ns13:userProfileResponse'.'ns13:userProfileResList'[i].'ns8:oldValue'.text() == cUL_expirationDate) && (resGetUser.'**'.'ns13:userProfileResponse'.'ns13:userProfileResList'[i].'ns8:newValue'.text() == uUL_expirationDate) && (resGetUser.'**'.'ns13:userProfileResponse'.'ns13:userProfileResList'[i].'ns8:action'.text() == "Update"))
			pass = pass+1;
			else
			//failMap.put(i+":"+resGetUser.'**'.'ns13:userProfileResponse'.'ns13:userProfileResList'[i].'ns8:newValue'.text(),uUL_expirationDate)
			failMap.put("Failure in UserLicense-Expiration Date Update","Actual:"+resGetUser.'**'.'ns13:userProfileResponse'.'ns13:userProfileResList'[i].'ns8:newValue'.text()+"--Expected:"+uUL_expirationDate)
		}

		}


		else if(resGetUser.'**'.'ns13:userProfileResponse'.'ns13:userProfileResList'[i].'ns8:attributeName'.text() =="State/Province/Region")

		{base = base+1;
		if (flagcreateUserLicenseState ==0)
		{
			if ((resGetUser.'**'.'ns13:userProfileResponse'.'ns13:userProfileResList'[i].'ns8:newValue'.text() == cUL_state) && (resGetUser.'**'.'ns13:userProfileResponse'.'ns13:userProfileResList'[i].'ns8:action'.text() == "Create"))
			{pass = pass+1;flagcreateUserLicenseState=1;}
			else
			{failMap.put("Failure in UserLicense-State","Actual:"+resGetUser.'**'.'ns13:userProfileResponse'.'ns13:userProfileResList'[i].'ns8:newValue'.text()+"--Expected:"+cUL_state)
			flagcreateUserLicenseState=1;}
		}
		else
		{log.info "update User Lic - State/Province/Region"
		if ((resGetUser.'**'.'ns13:userProfileResponse'.'ns13:userProfileResList'[i].'ns8:oldValue'.text() == cUL_state) && (resGetUser.'**'.'ns13:userProfileResponse'.'ns13:userProfileResList'[i].'ns8:newValue'.text() == uUL_state) && (resGetUser.'**'.'ns13:userProfileResponse'.'ns13:userProfileResList'[i].'ns8:action'.text() == "Update"))
			pass = pass+1;
			else
			failMap.put("Failure in UserLicense-State Update","Actual:"+resGetUser.'**'.'ns13:userProfileResponse'.'ns13:userProfileResList'[i].'ns8:newValue'.text()+"--Expected:"+uUL_state)
		}

		}
		else if(resGetUser.'**'.'ns13:userProfileResponse'.'ns13:userProfileResList'[i].'ns8:attributeName'.text() =="Professional License Number")

		{base = base+1;
			if ((resGetUser.'**'.'ns13:userProfileResponse'.'ns13:userProfileResList'[i].'ns8:newValue'.text() == cUL_proLicenseNum) && (resGetUser.'**'.'ns13:userProfileResponse'.'ns13:userProfileResList'[i].'ns8:action'.text() == "Create"))
			pass = pass+1;
			else
			failMap.put("Failure in UserLicense-Pro Lic","Actual:"+resGetUser.'**'.'ns13:userProfileResponse'.'ns13:userProfileResList'[i].'ns8:newValue'.text()+"--Expected:"+cUL_proLicenseNum)
		}

		else if(resGetUser.'**'.'ns13:userProfileResponse'.'ns13:userProfileResList'[i].'ns8:attributeName'.text() =="Issue Date")

		{base = base+1;
			if ((resGetUser.'**'.'ns13:userProfileResponse'.'ns13:userProfileResList'[i].'ns8:newValue'.text() == cUL_issueDate) && (resGetUser.'**'.'ns13:userProfileResponse'.'ns13:userProfileResList'[i].'ns8:action'.text() == "Create"))
			pass = pass+1;
			else
			failMap.put("Failure in UserLicense-Issue Date"+"Actual:"+resGetUser.'**'.'ns13:userProfileResponse'.'ns13:userProfileResList'[i].'ns8:newValue'.text()+"--Expected:"+cUL_issueDate)
		}

		else if(resGetUser.'**'.'ns13:userProfileResponse'.'ns13:userProfileResList'[i].'ns8:attributeName'.text() =="Type")

		{base = base+1;
			if ((resGetUser.'**'.'ns13:userProfileResponse'.'ns13:userProfileResList'[i].'ns8:newValue'.text() == cUL_typeofLicense) && (resGetUser.'**'.'ns13:userProfileResponse'.'ns13:userProfileResList'[i].'ns8:action'.text() == "Create"))
			pass = pass+1;
			else
			failMap.put("Failure in UserLicense-Type","Actual:"+resGetUser.'**'.'ns13:userProfileResponse'.'ns13:userProfileResList'[i].'ns8:newValue'.text()+"--Expected:"+cUL_typeofLicense)
		}

		else if(resGetUser.'**'.'ns13:userProfileResponse'.'ns13:userProfileResList'[i].'ns8:attributeName'.text() =="Country")

		{base = base+1;
			if ((resGetUser.'**'.'ns13:userProfileResponse'.'ns13:userProfileResList'[i].'ns8:newValue'.text() == "US") && (resGetUser.'**'.'ns13:userProfileResponse'.'ns13:userProfileResList'[i].'ns8:action'.text() == "Create"))
			pass = pass+1;
			else
			failMap.put("Failure in UserLicense-Country","Actual:"+resGetUser.'**'.'ns13:userProfileResponse'.'ns13:userProfileResList'[i].'ns8:newValue'.text()+"--Expected:"+cUL_countryId)
		}

	break;

		/*case updateUserLicense: // for createUserLicenses
		log.info "uUL-"+updateUserLicense
		if(resGetUser.'**'.'ns13:userProfileResponse'.'ns13:userProfileResList'[i].'ns8:attributeName'.text() =="State/Province/Region")

		{base = base+1;
			if ((resGetUser.'**'.'ns13:userProfileResponse'.'ns13:userProfileResList'[i].'ns8:oldValue'.text() == cUL_state) && (resGetUser.'**'.'ns13:userProfileResponse'.'ns13:userProfileResList'[i].'ns8:newValue'.text() == uUL_state) && (resGetUser.'**'.'ns13:userProfileResponse'.'ns13:userProfileResList'[i].'ns8:action'.text() == "Update"))
			pass = pass+1;
			else
			failMap.put(i+":"+resGetUser.'**'.'ns13:userProfileResponse'.'ns13:userProfileResList'[i].'ns8:newValue'.text(),uUL_state)
		}


		else if(resGetUser.'**'.'ns13:userProfileResponse'.'ns13:userProfileResList'[i].'ns8:attributeName'.text() =="Expiration Date")

		{base = base+1;
			if ((resGetUser.'**'.'ns13:userProfileResponse'.'ns13:userProfileResList'[i].'ns8:newValue'.text() == uUL_expirationDate) && (resGetUser.'**'.'ns13:userProfileResponse'.'ns13:userProfileResList'[i].'ns8:action'.text() == "Update"))
			pass = pass+1;
			else
			failMap.put(i+":"+resGetUser.'**'.'ns13:userProfileResponse'.'ns13:userProfileResList'[i].'ns8:newValue'.text(),uUL_expirationDate)
		}
		break;*/


		default:

		if (resGetUser.'**'.'ns13:userProfileResponse'.'ns13:userProfileResList'[i].'ns8:informationArea'.text() =="User Profile")
		{

		switch (resGetUser.'**'.'ns13:userProfileResponse'.'ns13:userProfileResList'[i].'ns8:attributeName'.text())
		{
			case "Name Initial":
			//log.info "Name Initial"
			if (uU_initialsFlag== false )
			{
			if ((resGetUser.'**'.'ns13:userProfileResponse'.'ns13:userProfileResList'[i].'ns8:newValue'.text() == uU_initials) && (resGetUser.'**'.'ns13:userProfileResponse'.'ns13:userProfileResList'[i].'ns8:action'.text() == "Update"))
			{uU_initialsFlag= true
			//log.info "Name Initial is success"
			}
			}//
			break;
			case "Street Name and Number":
			//log.info "Street Name and Number"
			if (uU_address1Flag== false )
			{
			if ((resGetUser.'**'.'ns13:userProfileResponse'.'ns13:userProfileResList'[i].'ns8:newValue'.text() == uU_address1) && (resGetUser.'**'.'ns13:userProfileResponse'.'ns13:userProfileResList'[i].'ns8:action'.text() == "Update"))
			{uU_address1Flag= true
			//log.info "Street name & no. is success"
			}
			}//
			break;

			case "City":
			//log.info "City
			if (uU_cityFlag== false )
			{
			if ((resGetUser.'**'.'ns13:userProfileResponse'.'ns13:userProfileResList'[i].'ns8:newValue'.text() == uU_city) && (resGetUser.'**'.'ns13:userProfileResponse'.'ns13:userProfileResList'[i].'ns8:action'.text() == "Update"))
			{uU_cityFlag= true
			//log.info "City is success"
			}
			}//
			break;

			case "Mobile/Pager/Fax Number":
			//log.info "City
			if (uU_phone1Flag== false )
			{
			if ((resGetUser.'**'.'ns13:userProfileResponse'.'ns13:userProfileResList'[i].'ns8:newValue'.text() == uU_phone1) && (resGetUser.'**'.'ns13:userProfileResponse'.'ns13:userProfileResList'[i].'ns8:action'.text() == "Update"))
			{uU_phone1Flag= true
			//log.info "City is success"
			}
			}//
			break;

		}//default Switch


		}//default if

		break;

	}// end of Switch statement



}//for end loop

//3. Fetching the Failure Data if any
if (uU_initialsFlag ==false)
		failMap.put("Failure in Update Initials","Expected:"+uU_initials)

if(uU_address1Flag== false )
		failMap.put("Failure in Update Street Name&Num","Expected:"+uU_address1)

if(uU_cityFlag== false )
		failMap.put("Failure in Update City","Expected:"+uU_city)

if(uU_phone1Flag== false )
		failMap.put("Failure in Update Mobile Number","Expected:"+uU_phone1)


def f = failMap.size();
log.info f
if (f!=0)
 {
	failMap.each
	{
		a,b-> 

		log.info a //Actual Data
		log.info b //Expected
		failedData.add(a+"--"+b)
	}
 	//if (base == pass || base == 0 || pass == 0)
 	//assert base == pass :"TC Failed :: Failed Entry="+f
 	for(def int ff=0;ff<failedData.size();ff++)
 	{
 	failedDataFull = failedDataFull+"Fail"+(ff+1)+"-"+failedData[ff]+";;"
 	}
 	 testRunner.testCase.setPropertyValue("Validation-Failed",failedDataFull)
 	 testRunner.fail("Mismatch in Audit Report")
 }

 else
 {
 log.info "All Updates made are successfully reflected in Audit Report "
 }

}// entry if
else
{
	log.info "Have No entries in User Profile Audit Report - make an entry & try again..."
}


//log.info resGetUser
}//if

else 
{
	testRunner.testCase.setPropertyValue("Validation-Failed", "Failure in running Test Step: "+StepName)
}
}//try

catch (Exception ex)

{
testRunner.testCase.setPropertyValue("Validation-Failed", "Failure in Test Step: "+StepName+" ::Err description:"+ex)
testRunner.fail("Failure in running the Target Step")
}

