			if (cRCTPI_phaseId == "1")cRCTPI_phaseId="I" else if (cRCTPI_phaseId == "2")cRCTPI_phaseId="II" else if (cRCTPI_phaseId == "3")cRCTPI_phaseId="III" else if (cRCTPI_phaseId == "4")cRCTPI_phaseId="IV"
			log.info "phase"+cCR_phaseId
			if ((resGetUser.'**'.'ns13:userProfileResponse'.'ns13:userProfileResList'[i].'ns8:oldValue'.text() == cRCTPI_phaseId) && (resGetUser.'**'.'ns13:userProfileResponse'.'ns13:userProfileResList'[i].'ns8:action'.text() == "Delete"))
			pass = pass+1;
			else
			failMap.put(i+":"+resGetUser.'**'.'ns13:userProfileResponse'.'ns13:userProfileResList'[i].'ns8:oldValue'.text(),cRCTPI_phaseId)

		}
		break;*/

	case createResearchTAI: // for createResearchTherapeuticAreasOfInterest
		log.info "cRTAI-" +createResearchTAI

		if(resGetUser.'**'.'ns13:userProfileResponse'.'ns13:userProfileResList'[i].'ns8:attributeName'.text() == "Therapeutic Area")
		{base = base+1;
		if (flagcreateResearchTAI ==0)
		{

			if ((resGetUser.'**'.'ns13:userProfileResponse'.'ns13:userProfileResList'[i].'ns8:newValue'.text() == "Nervous System Diseases") && (resGetUser.'**'.'ns13:userProfileResponse'.'ns13:userProfileResList'[i].'ns8:action'.text() == "Create"))
			{pass = pass+1;
			flagcreateResearchTAI=1;}
			else
			{failMap.put("Actual:"+resGetUser.'**'.'ns13:userProfileResponse'.'ns13:userProfileResList'[i].'ns8:newValue'.text(),"Expected:"+"Nervous System Diseases")
			flagcreateResearchTAI=1;}
		}

		else	
			{log.info "dRTAI"
			if ((resGetUser.'**'.'ns13:userProfileResponse'.'ns13:userProfileResList'[i].'ns8:oldValue'.text() == "Nervous System Diseases") && (resGetUser.'**'.'ns13:userProfileResponse'.'ns13:userProfileResList'[i].'ns8:action'.text() == "Delete"))
			pass = pass+1;
			else
			failMap.put("Actual:"+resGetUser.'**'.'ns13:userProfileResponse'.'ns13:userProfileResList'[i].'ns8:oldValue'.text(),"Expected:"+"Nervous System Diseases")
			}


		}
		break;

		/*case deleteResearchTAI: // for deleteResearchThera
		log.info "cRTAI"

		if(resGetUser.'**'.'ns13:userProfileResponse'.'ns13:userProfileResList'[i].'ns8:attributeName'.text() =="Therapeutic Area")

		{base = base+1;
			if ((resGetUser.'**'.'ns13:userProfileResponse'.'ns13:userProfileResList'[i].'ns8:oldValue'.text() == "Nervous System Diseases") && (resGetUser.'**'.'ns13:userProfileResponse'.'ns13:userProfileResList'[i].'ns8:action'.text() == "Delete"))
			pass = pass+1;
			else
			failMap.put(i+":"+resGetUser.'**'.'ns13:userProfileResponse'.'ns13:userProfileResList'[i].'ns8:newValue'.text(),"Nervous System Diseases")
		}

		break;*/


	case createUserLicense: // for createUserLicenses
		log.info "cUL-"+createUserLicense


		if (resGetUser.'**'.'ns13:userProfileResponse'.'ns13:userProfileResList'[i].'ns8:attributeName'.text() =="License Issuer")
		{base = base+1;
			if ((resGetUser.'**'.'ns13:userProfileResponse'.'ns13:userProfileResList'[i].'ns8:newValue'.text() == cUL_issuer) && (resGetUser.'**'.'ns13:userProfileResponse'.'ns13:userProfileResList'[i].'ns8:action'.text() == "Create"))
			pass = pass+1;
			else
			failMap.put("Actual:"+resGetUser.'**'.'ns13:userProfileResponse'.'ns13:userProfileResList'[i].'ns8:newValue'.text(),"Expected:"+cUL_issuer)
		}
		else if(resGetUser.'**'.'ns13:userProfileResponse'.'ns13:userProfileResList'[i].'ns8:attributeName'.text() =="Expiration Date")
