def rawdetails(def projPath, def projectname,def testsuitename,def testcasename, def testStep, def rawstep,def exectime)
{
 String strOutputReq =projPath+"/"+projectname+"/"+testsuitename+"/"+testcasename+"/Output/Raw Request"
 String strOutputRes =projPath+"/"+projectname+"/"+testsuitename+"/"+testcasename+"/Output/Raw Response"


 File g = new File(strOutputReq)
 boolean gg = g.exists();
 if (gg==false)
 {
  new File(strOutputReq).mkdirs()
  }
  File g1 = new File(strOutputRes)
 boolean gg1 = g1.exists();
 if (gg1==false)
 {
  new File(strOutputRes).mkdirs()
  }

 //boolean successOut = (new File(strOutput)).mkdir());
try
    {
     String reqRawStep=new String(testStep.getHttpRequest().response.rawRequestData)

     if(testcasename!=null && testcasename!="")
     {

      Writer w //log.info (" Request :"+testStep.getProperty('RawRequest').getValue()) workin
      //if(testcstep=="")
      //{
      // w = new OutputStreamWriter(new FileOutputStream(actrawpath+project+"/"+tsname+"/Output/"+testcname+"_Raw Request.txt")); 
      //}
      //else
      //{
       w = new OutputStreamWriter(new FileOutputStream(strOutputReq+"/"+testcasename+"_"+rawstep+"_Raw Request_"+exectime+".txt"));
      //}

      w.write(reqRawStep);
      w.close();         
     }
    }
    catch(Exception ex)
    {
     log.info("Exception in saving the raw request: "+ex)
    }
    try
    {
     String resRawStep=new String(testStep.getHttpRequest().response.rawResponseData)

     if(testcasename!=null && testcasename!="")
     {

      Writer w 


       w = new OutputStreamWriter(new FileOutputStream(strOutputRes+"/"+testcasename+"_"+rawstep+"_Raw Response_"+exectime+".txt"));


      w.write(resRawStep);
      w.close();         
     }
    }
    catch(Exception ex)
    {
     log.info("Exception in saving the raw response: "+ex)
    }
}
/*log.info "Step Size "+stepName.size()
for (def s=0;s<stepName.size();s++)
{
log.info "TestStep "+(s+1)+ "-" +stepName[s]
}
log.info "status Size "+stepStatus.size()
for (def j=0;j<stepStatus.size();j++)
{
log.info "TestStep Status "+(j+1)+ "-" +stepStatus[j]
}*/
