def createexcel(def projPath, def projectname, def testsuitename, def testcasename, def exectime)
{ 
def e = ""
try {
//log.info dirt.replace("\\","/")

 def actpath=projPath+"/"+projectname+"/"+testsuitename+"/"+testcasename 
 //def actpath=projPath+"/"+projectname+"/"+testsuitename  
 File g = new File(actpath)
 boolean gg = g.exists();
 if (gg==false)
 {
  new File(actpath).mkdirs()
  }


 def path = actpath+"/"+testcasename+"-Report_"+exectime+".xlsx"  
 //def path = actpath+"/"+testsuitename+"-Report_"+exectime+".xlsx" 
 log.info path
 runner.testSuite.setPropertyValue("TCreportpath",path)
  try {
  //log.info "inside"

  new File(path)

  }
  catch(Exception ex)
 {  
  log.info "Unexcepted Error occured while creating Report template:"+ex
  e= ex

 }

 /*try
 {  
  XSSFWorkbook wrkbk = Workbook.getWorkbook(new File(path))
 }
 catch(Exception ex)
 {  
  log.info "Unexcepted Error occured while creating Report template:"+ex
  e= ex
 }*/
  //log.info "e="+e
 if(e=="")
 {

  XSSFWorkbook wb = new XSSFWorkbook();
  //XSSFSheet sheet = wb.createSheet("Sheet1")
  //XSSFSheet tsheet = wb.createSheet("Sheet2")
  XSSFSheet rsheet = wb.createSheet("Response")
  //XSSFRow row = sheet.createRow((short)0);

  //Creating the headers for response in Response sheet.
  XSSFRow rrow = rsheet.createRow((short)0);
  XSSFCell rcelltsname = rrow.createCell(0);
  rcelltsname.setCellValue("TestSuite Name");

  XSSFCell rcelltcname = rrow.createCell(1);
  rcelltcname.setCellValue("TestCase Name");

  XSSFCell rcelltstname = rrow.createCell(2);    
  rcelltstname.setCellValue("StepName");

          XSSFCell rcell1 = rrow.createCell(3);
   rcell1.setCellValue("StepStatus"); 

   //XSSFCell rcell2 = rrow.createCell(4);
          //rcell2.setCellValue("Remarks");

          for(int i=0;i<5;i++)
          {
           rsheet.autoSizeColumn(i)
          }
  try
         {

           FileOutputStream fileOut = new FileOutputStream(path);
   wb.write(fileOut);
   fileOut.close();
         }
  catch(Exception ex)
  {
  log.info "Unexcepted Error occured while writing Header column in Report Sheet:"+ex 
  }
 }
}
catch(Exception ex)
{
log.info ("--Unexpected error occured in Create excel: " + ex.toString())
}
}
def updateexcel(def projectname,def testsuitename,def testcasename, def stepName, def stepStatus)
{

//log.info "TestCase " + testcasename
//log.info "step " + stepName.size()
//log.info "status " + stepStatus.size()

TargetPath = runner.testSuite.getPropertyValue("TCreportpath")
try
{
Class.forName("sun.jdbc.odbc.JdbcOdbcDriver");  //Target sheet for storing response
soapUIDataConnection = DriverManager.getConnection("jdbc:odbc:Driver={Microsoft Excel Driver (*.xls, *.xlsx, *.xlsm, *.xlsb)};DBQ=" + TargetPath + ";ReadOnly=0", "", "");
Statement UpdateStmt = ((Connection) soapUIDataConnection).createStatement();   
for (i=0;i<stepName.size();i++)
{
 try
 {
  String sQuery = "Insert into [Response\$] values ('" + testsuitename + "','" +testcasename + "','" + stepName[i] + "','" + stepStatus[i]+"')";
   log.info sQuery;
  UpdateStmt.executeUpdate(sQuery);

 }
 catch(Exception ex)
 {
  log.info("--Unexpected error occured while updating the value in Report Excel: " + ex.toString());
 }
}
soapUIDataConnection.close(); //Close data connection
UpdateStmt.close(); //Close statement
}
catch(Exception ex)
{
 log.info("--Unexpected error occured: " + ex.toString())
}
}

