def rawdetails(def projPath, def projectname,def testsuitename,def testcasename, def testStep, def rawstep,def exectime)
{
 String strOutputReq =projPath+"/"+projectname+"/"+testsuitename+"/"+testcasename+"/Output/Raw Request"
