 boolean gg1 = g1.exists();
 if (gg1==false)
 {
  new File(strOutputRes).mkdirs()
  }

 //boolean successOut = (new File(strOutput)).mkdir());
try
    {
     String reqRawStep=new String(testStep.getHttpRequest().response.rawRequestData)

     if(testcasename!=null && testcasename!="")
     {

      Writer w //log.info (" Request :"+testStep.getProperty('RawRequest').getValue()) workin
      //if(testcstep=="")
      //{
      // w = new OutputStreamWriter(new FileOutputStream(actrawpath+project+"/"+tsname+"/Output/"+testcname+"_Raw Request.txt")); 
      //}
      //else
      //{
       w = new OutputStreamWriter(new FileOutputStream(strOutputReq+"/"+testcasename+"_"+rawstep+"_Raw Request_"+exectime+".txt"));
      //}

      w.write(reqRawStep);
      w.close();         
     }
    }
    catch(Exception ex)
    {
     log.info("Exception in saving the raw request: "+ex)
    }
    try
    {
     String resRawStep=new String(testStep.getHttpRequest().response.rawResponseData)

