     {

      Writer w 


       w = new OutputStreamWriter(new FileOutputStream(strOutputRes+"/"+testcasename+"_"+rawstep+"_Raw Response_"+exectime+".txt"));


      w.write(resRawStep);
      w.close();         
     }
    }
    catch(Exception ex)
    {
     log.info("Exception in saving the raw response: "+ex)
    }
}
